package org.example;

import io.substrait.extension.SimpleExtension;
import io.substrait.isthmus.SubstraitToCalcite;
import io.substrait.isthmus.SubstraitToSql;
import io.substrait.isthmus.SubstraitTypeSystem;
import io.substrait.proto.Plan;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import org.apache.calcite.avatica.util.Base64;
import org.apache.calcite.jdbc.JavaTypeFactoryImpl;

public class App {

  /** Arbitary Substrait plan encoded as base 64 */
  public static final String base64Plan =
      "";

  public static void main(String[] args) {
    var app = new App();

    app.substraitToSql().forEach(System.out::println);
  }

  public List<String> substraitToSql() {

    List<String> sql = new ArrayList<>();
    try {
      Plan protoPlan = Plan.parseFrom(Base64.decode(base64Plan));

      var converter = new io.substrait.plan.ProtoPlanConverter();
      io.substrait.plan.Plan pojoPlan = converter.from(protoPlan);

      System.out.println("POJO Substrait Plan::\n" + pojoPlan);
      SimpleExtension.ExtensionCollection extensions = SimpleExtension.loadDefaults();
      var substrait2Calcite =
          new SubstraitToCalcite(
              extensions, new JavaTypeFactoryImpl(SubstraitTypeSystem.TYPE_SYSTEM));

      for (var root : pojoPlan.getRoots()) {
        var calciteRel = substrait2Calcite.convert(root).project();
        sql.add(SubstraitToSql.toSql(calciteRel));
      }

    } catch (IOException e) {
      e.printStackTrace(System.out);
    }
    return sql;
  }
}
