package io.substrait.examples;

import io.substrait.extension.SimpleExtension;
import io.substrait.isthmus.SubstraitToCalcite;
import io.substrait.isthmus.SubstraitToSql;
import io.substrait.isthmus.SubstraitTypeSystem;
import io.substrait.plan.ProtoPlanConverter;
import io.substrait.proto.Plan;
import java.util.Base64;
import org.apache.calcite.jdbc.JavaTypeFactoryImpl;
import org.junit.jupiter.api.Test;

import com.google.protobuf.InvalidProtocolBufferException;

class IsthmusApiTest {

  /** Arbitary Substrait plan encoded as base 64 */
  public static final String base64Plan = "";

  @Test
  void substraitToSqlviaCalcite() {

    /** Excplitily convert via calcite to sql */
    try {

      Plan protoPlan = Plan.parseFrom(Base64.getDecoder().decode(base64Plan));

      ProtoPlanConverter converter = new io.substrait.plan.ProtoPlanConverter();
      io.substrait.plan.Plan pojoPlan = converter.from(protoPlan);

      System.out.println("POJO Substrait Plan::\n" + pojoPlan);

      SimpleExtension.ExtensionCollection extensions = SimpleExtension.loadDefaults();

      SubstraitToCalcite substrait2Calcite = new SubstraitToCalcite(
          extensions, new JavaTypeFactoryImpl(SubstraitTypeSystem.TYPE_SYSTEM));

      for (var root : pojoPlan.getRoots()) {
        var calciteRel = substrait2Calcite.convert(root).project();
        String sql = SubstraitToSql.toSql(calciteRel);
        System.out.println(sql);
      }

    } catch (InvalidProtocolBufferException e) {
      e.printStackTrace();
    }
  }

  @Test
  void substraitToSql() {

    /** Excplitily convert via calcite to sql */
    try {

      Plan protoPlan = Plan.parseFrom(Base64.getDecoder().decode(base64Plan));

      ProtoPlanConverter converter = new io.substrait.plan.ProtoPlanConverter();
      io.substrait.plan.Plan pojoPlan = converter.from(protoPlan);

      System.out.println("POJO Substrait Plan::\n" + pojoPlan);

      for (String sql : SubstraitToSql.toSql(pojoPlan)) {
        System.out.println(sql);
      }

    } catch (InvalidProtocolBufferException e) {
      e.printStackTrace();
    }
  }
}
