SELECT vehicles.vehicle_id AS vehicle_id, vehicles.make AS make, vehicles.model AS model, vehicles.colour AS colour, vehicles.fuel_type AS fuel_type, vehicles.cylinder_capacity AS cylinder_capacity, vehicles.first_use_date AS first_use_date, tests.test_id AS test_id, tests.vehicle_id AS vehicle_id0, tests.test_date AS test_date, tests.test_type AS test_type, tests.test_class AS test_class, tests.test_result AS test_result, tests.test_mileage AS test_mileage, tests.postcode_area AS postcode_area FROM vehicles INNER JOIN tests ON vehicles.vehicle_id = tests.vehicle_id WHERE tests.test_result = 'F' AND tests.test_mileage < 70000